mapper.select:
SELECT EXISTS (
SELECT 1
FROM `User`
WHERE `username` = '입력할_사용자이름'
) AS user_exists;

mapper.update:
UPDATE Employee
SET column_name = new_value
WHERE id = #{id}

mapper.select:
select emp_comp_no
from employee
where emp_code = #{emp_code}

SELECT * FROM paperless.employee
         WHERE emp_comp_no = #{emp_comp_no}
         AND emp_role != 'super'
